mapper.select:
SELECT BBS_NO, EDITOR, TITLE, CONTENTS, CREATED_AT, MODIFIED_AT
  FROM BBS_T
 WHERE BBS_NO = #{bbsNo}

mapper.insert:
INSERT INTO BBS_T(
    BBS_NO
  , EDITOR
  , TITLE
  , CONTENTS
  , CREATED_AT
  , MODIFIED_AT
) VALUES(
    BBS_SEQ.NEXTVAL
  , #{editor}
  , #{title}
  , #{contents}
  , SYSDATE
  , NULL
)

mapper.update:
UPDATE BBS_T
   SET TITLE = #{title}
     , CONTENTS = #{contents}
     , MODIFIED_AT = SYSDATE
 WHERE BBS_NO = #{bbsNo}
